import { useRef, useState } from "react"

const App = () => {

  const [num, setNum] = useState(0)
  const email = useRef()

  const calculateLong = e => {
    setNum(e.target.value.length)
  }

  const submit = e => {
    e.preventDefault() 
    alert("Formulario Enviado")
    console.log(email.current.value)
  }
  return (
    <div>
      <form onSubmit={submit}>
        <input 
        type="email" 
        defaultValue="julian@gmail.com" 
        ref={email}
        />
        <input 
        type="password" 
        placeholder="ContraseÃ±a" 
        required
        onChange={calculateLong}
        />
        <p>Usted Ingreso {num} caracteres</p>
        <label htmlFor="remember">Recuerdame</label>
        <input type="checkbox" name="remember" id="remember" defaultChecked/>
        <select name="country" id="country" defaultValue="colombia" required>
          <option value="bolivia">Bolivia</option>
          <option value="colombia">Colombia</option>
          <option value="argentina">Argentina</option>
          <option value="brasil">Brasil</option>
          <option value="uruguay">Uruguay</option>
        </select>

        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
}

export default App;
