import { useState } from "react";
import "./App.scss";
/*Esto importa el useStategancho del reactpaquete 
  e importa los estilos del App.scssarchivo. */
const App = () => {

  const [data, setData] = useState({ email: "", password: ""})
  /*Esta línea inicializa una variable de estado llamada 
  data y una función para actualizar esa variable de estado 
  llamada setData. El useStategancho se usa para crear esta variable
   de estado y establecer su valor inicial en un objeto con emailpropiedades 
   password establecidas en cadenas vacías. */

  const changeData = e => {
    setData({
      ...data,
      [e.target.name]: e.target.value
    })
  }
  /*Esta es una función de controlador de eventos que actualiza la datavariable de 
  estado cuando el usuario escribe algo en los campos de correo electrónico o contraseña. 
  Utiliza las propiedades e.target.namey e.target.valuepara actualizar la propiedad adecuada 
  en el dataobjeto. */

  const submit = e => {
    e.preventDefault()
    alert("¡Bienvenido!")
    console.log(data)
    /*Esta es una función de controlador de eventos que se llama cuando el usuario envía el formulario. 
    Evita el comportamiento predeterminado de envío de formularios y muestra una alerta con un mensaje de bienvenida.*/
  }
  return (
  <div className="ed-grid">
    <form className="ed-container l-30" onSubmit={submit}>
      <div className="ed-item form__item">
        <label htmlFor="email">
          Correo Electronico
          <input 
          type="email" 
          name="email" 
          id="email" 
          value={data.email} 
          onChange={changeData} 
          />
        </label>
      </div>
      <div className="ed-item form__item">
        <label htmlFor="password">
          Contraseña
          <input 
          type="password" 
          name="password" 
          id="password"
          value={data.password}
          onChange={changeData}
          />
        </label>
      </div>
      <div className="ed-item form__item"></div>
      <input type="submit" value="Ingresar" className="button full"/>
    </form>
  </div>
  )
}
/*Este es el código JSX que define la estructura y el comportamiento del formulario. El formulario está envuelto en un 
div elemento con un classNamede ed-grid. El formulario en sí es un ed-containerelemento con un ancho del 30% y contiene dos 
campos de formulario para correo electrónico y contraseña. Cada campo de entrada está envuelto en un labelelemento y tiene un 
onChangeatributo que llama a la changeDatafunción. La submitfunción se llama cuando se envía el formulario, como lo indica el 
onSubmit atributo en el formelemento. */

export default App;
/*Esto exporta el Appcomponente como la exportación predeterminada, por lo que se puede usar en otras partes de la base de código. */
